    totalOrders: 0,
    totalOrdersThisMonth: 0,
    totalOrdersInProgress: 260,
    revenue: 0,
    expectedRevenue: 0,
    recentOrders: []
}

export function Data() {

    const [metrics, setMetrics] = useState(defaultMetrics);
