import {useReducer} from "react";
import ButtonDigitTop from "./ButtonDigitTop";
import ButtonDigitRight from "./ButtonDigitRight";
import ButtonDigitCenter from "./ButtonDigitCenter";
import ButtonDigitCenterZero from "./ButtonDigitCenterZero";
import './index.css';
import ButtonOperation from "./ButtonOperration";

export const ACTIONS = {
  ADD_DIGIT: 'add-digit',
  CHOOSE_OPERATION: 'choose-operation',
  CLEAR: 'clear',
  EVALUATION: 'evaluate'

}

function reducer(state, {type, payload}){
  switch (type){
      //return a new state
    case ACTIONS.ADD_DIGIT:
      //returns the current state of 0 if the user's first input is zero
      if(payload.digit === "0" && state.currentOperand ==="0"){
        return state
    }
        //returns a new state as a decimal number
      if(payload.digit === "." && state.currentOperand.includes(".")) {
        return state
      }
      return{
        ...state,
        //takes the current operand and adds the digit at the end of it
        //returns an empty string if the operand is null
          currentOperand: `${state.currentOperand || "" }${payload.digit}`,
      }
      //returns an empty state
    case ACTIONS.CLEAR:
      return {}
    case ACTIONS.CHOOSE_OPERATION:
      if(state.currentOperand == null && state.previousOperand == null) {
      return state
      }
      //overwrites the operation
      if (state.currentOperand== null)
        return{
        ...state,
          operation: payload.operation,
        }
      if (state.previousOperand == null){
        return {
          ...state,
          operation: payload.operation,
          previousOperand: state.currentOperand,
          currentOperand: null,
        }
      }
      return {
        ...state,
        previousOperand: evaluate(state),
        operation: payload.operation,
        currentOperand: null
      }
    case ACTIONS.EVALUATION:
    if(state.operation == null || state.currentOperand == null|| state.previousOperand == null)
    {
      return state
    }

  }
}

function evaluate({ currentOperand, previousOperand, operation}){
  const prev = parseFloat(previousOperand)
  const current = parseFloat(currentOperand)
  if (isNaN(prev) || isNaN(current)) return""
  let computation = ""
  switch (operation){
    case "+":
        computation = prev + current
          break
    case "-":
      computation = prev - current
      break
    case "*":
      computation = prev * current
      break
    case "รท":
      computation = prev / current
      break
  }
  return computation.toString()
}

function App() {

  const [{currentOperand, previousOperand, operation}, dispatch] = useReducer(reducer, {})

    return (
          <div className="calculator-display">

            <div className="calculator-operation-output">
              <div className="previous-operand">{previousOperand} {operation}</div>
              <div className="current-operand">{currentOperand}</div>
            </div>

            <div className="calculator-grid">

              <button className="button-display top" onClick={() => dispatch({type: ACTIONS.CLEAR})}>AC</button>
              <ButtonDigitTop digit = "ยฑ" dispatch={dispatch}/>
              <ButtonOperation operation = "%" dispatch={dispatch}/>
              <ButtonOperation operation= "รท" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "7" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "8" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "9" dispatch={dispatch}/>
              <ButtonOperation operation = "x" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "4" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "5" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "6" dispatch={dispatch}/>
              <ButtonOperation operation = "-" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "1" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "2" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "3" dispatch={dispatch}/>
              <ButtonOperation operation = "+" dispatch={dispatch}/>

              <ButtonDigitCenterZero digit = "0" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "." dispatch={dispatch}/>
              <button className="button-display right" onClick={() => dispatch({type: ACTIONS.EVALUATION})}>=</button>

          </div>
         </div>
    )

}
export default App;
