import {useReducer} from "react";
import ButtonDigitTop from "./ButtonDigitTop";
import ButtonDigitRight from "./ButtonDigitRight";
import ButtonDigitCenter from "./ButtonDigitCenter";
import ButtonDigitCenterZero from "./ButtonDigitCenterZero";
//import './index.css';

export const ACTIONS = {
  ADD_DIGIT: 'add-digit',
  CHOOSE_OPERATION: 'choose-operation',
  CLEAR: 'clear',
  EVALUATION: 'evaluate'

}

function reducer(state, {type, payload}){
  switch (type){
      //return a new state
    case ACTIONS.ADD_DIGIT:
      return{
        ...state,
        //takes the current operand and adds the digit at the end of it
        //returns an empty string if the operand is null
          currentOperand: `${state.currentOperand || "" }${payload.digit}`,
      }

  }
}

function App() {

  const [{currentOperand, previousOperand, operation}, dispatch] = useReducer(reducer, {})

    return (
        <div className="calculator">
          <div className="calculator-display">

            <div className="calculator-operation-output">
              <div className="previous-operand">{previousOperand} {operation}</div>
              <div className="current-operand">{currentOperand}</div>
            </div>

            <div className="calculator-grid">

              <ButtonDigitTop digit = "AC" dispatch={dispatch}/>
              <ButtonDigitTop digit = "±" dispatch={dispatch}/>
              <ButtonDigitTop digit = "%" dispatch={dispatch}/>
              <ButtonDigitRight digit = "÷" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "7" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "8" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "9" dispatch={dispatch}/>
              <ButtonDigitRight digit = "x" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "4" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "5" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "6" dispatch={dispatch}/>
              <ButtonDigitRight digit = "-" dispatch={dispatch}/>

              <ButtonDigitCenter digit = "1" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "2" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "3" dispatch={dispatch}/>
              <ButtonDigitRight digit = "+" dispatch={dispatch}/>

              <ButtonDigitCenterZero digit = "0" dispatch={dispatch}/>
              <ButtonDigitCenter digit = "." dispatch={dispatch}/>
              <ButtonDigitRight digit = "=" dispatch={dispatch}/>
          </div>
         </div>
        </div>
    )

}
export default App;
