import {ACTIONS} from "./App"
//takes an argument pass the dispatch to call the reducer
export default function ButtonDigit({dispatch, digit}){
    //calls the dispatch function
    return(
        <button class="button-display top right"
            onClick={() => dispatch({ type: ACTIONS.ADD_DIGIT, payload: {digit}})}
                >
            {digit}
        </button>
    )
}
